local espActive=false local RunService=game:GetService("RunService")local Players=game:GetService("Players")local LP=Players.LocalPlayer buttons["ESP"].MouseButton1Click:Connect(function()espActive=not espActive if espActive then for _,p in pairs(Players:GetPlayers())do if p~=LP and p.Character and p.Character:FindFirstChild("HumanoidRootPart")then local bb=Instance.new("BillboardGui",p.Character.HumanoidRootPart)bb.Name="HakaiESP"bb.Size=UDim2.new(0,200,0,50)bb.Adornee=p.Character.HumanoidRootPart bb.AlwaysOnTop=true local txt=Instance.new("TextLabel",bb)txt.Size=UDim2.new(1,0,1,0)txt.BackgroundTransparency=1 txt.Font=Enum.Font.SourceSansBold txt.TextColor3=Color3.fromRGB(255,255,255)txt.TextStrokeTransparency=0 txt.TextSize=16 txt.Text=string.format("%s\n%dM\n%d",p.Name,math.floor((LP.Character.HumanoidRootPart.Position-p.Character.HumanoidRootPart.Position).Magnitude),math.floor(p.Character.Humanoid.Health))RunService.Heartbeat:Connect(function()if p and p.Character and p.Character:FindFirstChild("HumanoidRootPart")then txt.Text=string.format("%s\n%dM\n%d",p.Name,math.floor((LP.Character.HumanoidRootPart.Position-p.Character.HumanoidRootPart.Position).Magnitude),math.floor(p.Character.Humanoid.Health))end end)end end else for _,p in pairs(Players:GetPlayers())do if p.Character and p.Character:FindFirstChild("HumanoidRootPart")then local e=p.Character.HumanoidRootPart:FindFirstChild("HakaiESP")if e then e:Destroy()end end end end end)

buttons["Camlock"].MouseButton1Click:Connect(function()local cam=workspace.CurrentCamera local CamlockEnabled=false local LockedTarget=nil local function GetTarget()local cp=nil local cd=math.huge for _,p in ipairs(Players:GetPlayers())do if p~=LP and p.Character and p.Character:FindFirstChild("HumanoidRootPart")and p.Character.Humanoid.Health>0 then local pos,vis=cam:WorldToViewportPoint(p.Character.HumanoidRootPart.Position)if vis then local sc=Vector2.new(cam.ViewportSize.X/2,cam.ViewportSize.Y/2)local dist=(sc-Vector2.new(pos.X,pos.Y)).Magnitude if dist<cd then cd=dist cp=p end end end end return cp end local b=Instance.new("ScreenGui",game.CoreGui)local t=Instance.new("TextButton",b)t.Size=UDim2.new(0,160,0,40)t.Position=UDim2.new(0.5,-80,0.9,0)t.Text="Camlock: OFF"t.BackgroundColor3=Color3.fromRGB(50,50,50)t.TextColor3=Color3.fromRGB(255,255,255)t.Font=Enum.Font.SourceSansBold t.TextSize=20 t.Active=true t.Draggable=true local d=Instance.new("TextButton",b)d.Size=UDim2.new(0,40,0,40)d.Position=UDim2.new(0,0,0,0)d.Text="X"d.BackgroundColor3=Color3.fromRGB(60,60,60)d.TextColor3=Color3.fromRGB(255,255,255)d.Font=Enum.Font.SourceSansBold d.TextSize=18 d.MouseButton1Click:Connect(function()b:Destroy()end) t.MouseButton1Click:Connect(function()CamlockEnabled=not CamlockEnabled if CamlockEnabled then LockedTarget=GetTarget()if LockedTarget then t.Text="Locked: "..LockedTarget.Name else t.Text="No Target"CamlockEnabled=false end else LockedTarget=nil t.Text="Camlock: OFF"end end)RunService.RenderStepped:Connect(function()if CamlockEnabled and LockedTarget and LockedTarget.Character and LockedTarget.Character:FindFirstChild("HumanoidRootPart")then cam.CFrame=CFrame.new(cam.CFrame.Position,LockedTarget.Character.HumanoidRootPart.Position)end end)end)

