buttons["Server Hop"].MouseButton1Click:Connect(function()local ts=game:GetService("TeleportService")local hs=game:GetService("HttpService")local lp=Players.LocalPlayer local PlaceId=game.PlaceId local JobId=game.JobId local servers={}local cursor=""local req=syn and syn.request or http_request or request local s,r=pcall(function()return req({Url="https://games.roblox.com/v1/games/"..PlaceId.."/servers/Public?limit=100&sortOrder=Asc&cursor="..cursor})end)if s and r and r.Body then local d=hs:JSONDecode(r.Body)for _,sv in pairs(d.data)do if sv.playing<sv.maxPlayers and sv.id~=JobId then table.insert(servers,sv.id)end end if #servers>0 then local rand=servers[math.random(1,#servers)]ts:TeleportToPlaceInstance(PlaceId,rand,lp)else warn("No servers.")end else warn("Fetch fail.")end end)

